import React, {useState} from "react";
import Card from './Card'

const Form = () => {
  const [user,setUser] = useState({
      userName: '',
      email:'',
  })
  const [show,setShow] = useState(false)
  const [err,setErr] = useState(false)

  const handleSubmit = (e) =>{
          e.preventDefault()
          if(user.userName.length > 5  ){
              setShow(true)
              setErr(false)
          }else{
              setShow(false)
              setErr(true)
          }
  }


  return(
      <div>              
              <form onSubmit={handleSubmit}>
                  <input placeholder='Ingrese su nombre de usuario' type="text" value={user.userName} onChange={(e)=> setUser({...user, userName: e.target.value})} />
                  <input placeholder='Ingrese su e-mail' type="email" value={user.email} onChange={(e)=> setUser({...user, email: e.target.value})}/>

                  <button>Enviar</button>
              </form>
              <br />
              <p style={{color:'red'}}>{err && 'Por favor verifique su información nuevamente'}</p>
          {show &&  <Card userName={user.userName} />}
      </div>

  )
}

export default Form;
