import React, {useState} from 'react'
import Card from './Card'

const Form = () => {
  //Aqui deberan implementar el form completo con sus validaciones
  const [user,setUser] = useState({
    nombreUsuario: '',
    email:''
    })

    const [show,setShow] = useState(false)
    const [err,setErr] = useState(false)

const handleSubmit = (e) =>{
        e.preventDefault()
        if(user.nombreUsuario.length > 5){
            setShow(true)
            setErr(false)
        }else{
            setShow(false)
            setErr(true)
        }
}

  return (
    <div>
        <form onSubmit={handleSubmit}>
            <input placeholder='Ingrese su nombre' type="text" value={user.nombreUsuario} onChange={(e)=> setUser({...user, nombreUsuario: e.target.value})} />
            <input placeholder='Ingrese su e-mail' type="email" value={user.email} onChange={(e)=> setUser({...user, email: e.target.value})}/>
            <button style={{color:'white', backgroundColor:'blue', width:'150px', margin: '10%',padding: '10%'}}>Enviar</button>
        </form>
        <p style={{color:'red'}}>{err && 'Por favor chequea que la información sea correcta'}</p>
    </div>
  );
};

export default Form;
